import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { MaterialCommunityIcons } from '@expo/vector-icons'; // Assuming Expo for icons
import { useNavigation } from '@react-navigation/native';
import { NativeStackNavigationProp } from '@react-navigation/native-stack';
import { RootStackParamList } from '../navigation/types';

type NavigationProp = NativeStackNavigationProp<RootStackParamList, 'Landing'>;

const LandingScreen: React.FC = () => {
  const navigation = useNavigation<NavigationProp>();

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.logo}>Fluently</Text>
        <TouchableOpacity style={styles.loginButton}>
          <Text style={styles.loginButtonText}>Log in</Text>
        </TouchableOpacity>
      </View>

      <View style={styles.content}>
        <Text style={styles.title}>What would you like</Text>
        <Text style={styles.titleAccent}>to improve</Text>
        <Text style={styles.title}>in your English?</Text>

        <View style={styles.optionsContainer}>
          <TouchableOpacity style={styles.optionButton}>
            <MaterialCommunityIcons name="microphone" size={24} color="#ffffff" />
            <Text style={styles.optionButtonText}>pronunciation</Text>
            <View style={styles.radioButton} />
          </TouchableOpacity>

          <TouchableOpacity style={styles.optionButton}>
            <MaterialCommunityIcons name="pencil" size={24} color="#ffffff" />
            <Text style={styles.optionButtonText}>grammar</Text>
            <View style={styles.radioButton} />
          </TouchableOpacity>

          <TouchableOpacity style={styles.optionButton}>
            <MaterialCommunityIcons name="book-open" size={24} color="#ffffff" />
            <Text style={styles.optionButtonText}>vocabulary</Text>
            <View style={styles.radioButton} />
          </TouchableOpacity>

          <TouchableOpacity style={styles.optionButton}>
            <MaterialCommunityIcons name="waveform" size={24} color="#ffffff" />
            <Text style={styles.optionButtonText}>fluency</Text>
            <View style={styles.radioButton} />
          </TouchableOpacity>
        </View>

        <TouchableOpacity style={styles.improveButton}>
          <Text style={styles.improveButtonText}>Let's improve</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#0a0a23', // Dark background color
    padding: 20,
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    width: '100%',
    marginTop: 20, // Adjust as needed for status bar
  },
  logo: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#ffffff',
  },
  loginButton: {
    paddingVertical: 8,
    paddingHorizontal: 15,
    backgroundColor: '#1e90ff', // Blue button color
    borderRadius: 5,
  },
  loginButtonText: {
    color: '#ffffff',
    fontSize: 16,
  },
  content: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: -50, // Adjust to move content up
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#ffffff',
    textAlign: 'center',
  },
  titleAccent: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#1e90ff', // Blue color for 'improve'
    textAlign: 'center',
    marginBottom: 20,
  },
  optionsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
    width: '100%',
    maxWidth: 400, // Limit width for larger screens
    marginBottom: 30,
  },
  optionButton: {
    backgroundColor: '#1a1a1a', // Darker background for buttons
    borderRadius: 10,
    padding: 15,
    width: '48%', // Roughly half with some space
    marginBottom: 15, // Space between rows
    flexDirection: 'row',
    alignItems: 'center',
  },
  optionButtonText: {
    color: '#ffffff',
    fontSize: 16,
    fontWeight: 'bold',
    marginLeft: 10,
    flex: 1, // Take up remaining space
  },
  radioButton: {
    width: 20,
    height: 20,
    borderRadius: 10,
    borderWidth: 2,
    borderColor: '#ffffff', // White border
    marginLeft: 10,
  },
  improveButton: {
    paddingVertical: 15,
    paddingHorizontal: 30,
    backgroundColor: '#ffffff', // White button color
    borderRadius: 5,
  },
  improveButtonText: {
    color: '#0a0a23', // Dark text color
    fontSize: 18,
    fontWeight: 'bold',
  },
});

export default LandingScreen;
